    return (
      <CardsContainer>
        {texts.cards.map((card, idx) => (
          <Card key={idx}>
            <CardImage src={card.image} alt={card.alt} />
            <TitleContainer>
              <Text type="h3">{card.title}</Text>
            </TitleContainer>
            <Text type="p">{card.description}</Text>
          </Card>
        ))}
      </CardsContainer>
    )
  }

  renderFooter = () => {
        const { button, priceTagline } = this.props.texts

    return (
      <Footer>
