  background-color: ${gray};
  color: ${white};
  cursor: pointer;
`

/** Left Aligned with Top vertical alignment */
export default function ExampleTooltip() {
  return (
    <Tooltip
      label="My top aligned Tooltip "
      position="left"
      verticalAlign="top"
    >
      <Wrapper>
        <Text type="label">Content</Text>
      </Wrapper>
    </Tooltip>
  )
}

